import React from 'react';

// import { ReactComponent as Logo } from '../../assets/logo.svg';
// <Logo className='logo' />

import { HeaderContainer, LogoContainer, OptionsContainer, OptionLink } from './header.styles';

const Header = ( { currentUser, hidden } ) => (
    <HeaderContainer>
        <LogoContainer to="/">
        </LogoContainer>
        <OptionsContainer>
          <OptionLink to='/shop'>
              SHOP
          </OptionLink>
          <OptionLink to='/shop'>
              CONTACT
          </OptionLink>
          { currentUser ? 
            (<OptionLink as='div'>
                SIGN OUT
            </OptionLink>
            ) : (
            <OptionLink to='/signin'>
                SIGN IN
            </OptionLink>
            )}
        </OptionsContainer>
    </HeaderContainer>
);

export default (Header);
